function addLine(o,a){a.setMag(20+random(60)),o.add(a),vertex(o.x,o.y)}function addCurve(o,a){const n=(random(1)<.5?1:-1)*(random(1)<.7?HALF_PI:QUARTER_PI);a.setMag(.5);for(let e=0;e<50;e+=1)a.rotate(n/50),o.add(a),vertex(o.x,o.y)}function drawSomething(){const o=createVector(random(.3,.7)*width,random(.3,.7)*height),a=p5.Vector.fromAngle(Math.floor(random(8))*QUARTER_PI);stroke(drawingColor),noFill(),beginShape(),vertex(o.x,o.y),addLine(o,a);for(let n=0,e=Math.floor(random(4));n<e;n+=1)addCurve(o,a),addLine(o,a);endShape()}function drawDot(){noStroke(),fill(drawingColor),ellipse(random(.3,.7)*width,random(.3,.7)*height,10,10)}function setup(){const o=Math.min(windowWidth,windowHeight);canvas=createCanvas(o,o),canvasScaleFactor=o/640,noLoop(),drawingColor=color(80),strokeWeight(5)}function draw(){background(252,255,248),scale(canvasScaleFactor);for(let o=0;o<2;o+=1)drawSomething();drawDot()}function mouseClicked(){redraw()}function keyPressed(){keyCode===ENTER&&saveCanvas(canvas,"something","png")}let canvas,canvasScaleFactor,drawingColor;